sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
		if ( newValue == '') {
			return;
		}

		var whatneeded = g_form.getValue('u_what_needed');

		// Borra todas las opciones de la lista de opciones del campo What needed
		g_form.clearOptions('u_what_needed');

		// Si el valor del campo Request type es hr, agrega dos opciones de hr
		// y otra a la lista de selección del campo What needed
		if(newValue == 'hr'){
			g_form.addOption('u_what_needed','hr1','Human Resources 1');
			g_form.addOption('u_what_needed','hr2','Human Resources 2');
			g_form.addOption('u_what_needed','other','Other');
		}
		// Si el valor del campo Request type es facilities, agrega dos opciones de
		// facilities y otra a la lista de seleccion del campo What needed
		if(newValue == 'facilities'){
			g_form.addOption('u_what_needed','facilities1','Facilities 1');
			g_form.addOption('u_what_needed','facilities2','Facilities 2');
			g_form.addOption('u_what_needed','other','Other');
		}
		// Si el valor del campo Request type es legal, agrega dos opciones de
		// legal y otra a la lista de seleccion del campo What needed
		if(newValue == 'legal'){
			g_form.addOption('u_what_needed','legal1','Legal 1');
			g_form.addOption('u_what_needed','legal2','Legal 2');
			g_form.addOption('u_what_needed','other','Other');
		}

		//Si el formulario se está cargando y no es un registro nuevo, establezca el valor                 //de u_what_needed al valor del registro antes de cargarlo
		if(isLoading && !g_form.isNewRecord()){
			g_form.setValue('u_what_needed', whatneeded);
		}
	}
