function validateEmailAddress(emailStr){
		// Usa la coacci贸n de JavaScript para garantizar que emailStr sea una cadena
		emailStr = emailStr + '';
		// Compare emailStr contra la sintaxis permitida como se especifica en la expresi贸n regular
		// Si emailStr ha permitido la sintaxis, devuelve verdadero, si no, devuelve falso
		if(emailStr.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/)){
			return true;
		}
		else {
		 return false;
		}
	}
